Scripts.OnLoad:
local name = self:GetName();
self.points = getglobal(name .. "Points");
self.icon = getglobal(name .. "Icon");

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if ( self.name ) then
	GameTooltip:AddDoubleLine(self.name, self.date, nil, nil, nil, .5, .5, .5);
end
if ( self.desc ) then
	GameTooltip:AddLine(self.desc, 1, 1, 1);
end
if ( self.numCriteria ) then
	for i = 1, self.numCriteria do
		GameTooltip:AddLine(self["criteria"..i]);
	end
end
GameTooltip:Show();

Scripts.OnLoad:
local name = self:GetName();
self.icon = getglobal(name .. "Icon");
self.label = getglobal(name .. "Label");
self.check = getglobal(name .. "Check");
self.border = getglobal(name .. "Border");

Scripts.OnEnter:
if ( self.date ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:AddLine(self.date, 1, 1, 1);
	GameTooltip:Show();
end

Scripts.OnLoad:
local name = self:GetName();
self.name = getglobal(name .. "Name");
self.check = getglobal(name .. "Check");

Scripts.OnLeave:
if ( not self.selected ) then
	self.highlight:Hide();
end

Scripts.OnLoad:
local name = self:GetName();
self.background = getglobal(name.."BG");
self.background:SetAlpha(0.5);
self.name = getglobal(name.."Name");
self.date = getglobal(name.."Date");
self.points = getglobal(name.."Points");
self.icon = getglobal(name.."Icon");
AchievementFrameSummaryAchievements.buttons = AchievementFrameSummaryAchievements.buttons or {};
tinsert(AchievementFrameSummaryAchievements.buttons, self);
