Scripts.OnClick:
if ( self.sortType ) then
	SortGuildRoster(self.sortType);
	GuildRoster_Update();
end				
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnMouseDown:
if ( self:IsEnabled() ) then
	local name = self:GetName();
	_G[name.."Left"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Down");
	_G[name.."Middle"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Down");
	_G[name.."Right"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Down");
end
self.arrow:SetPoint("RIGHT", -13, -1);

Scripts.OnMouseUp:
if ( self:IsEnabled() ) then
	local name = self:GetName();
	_G[name.."Left"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Up");
	_G[name.."Middle"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Up");
	_G[name.."Right"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Up");
end
self.arrow:SetPoint("RIGHT", -14, 0);

Scripts.OnShow:
if ( self:IsEnabled() ) then
	-- we need to reset our textures just in case we were hidden before a mouse up fired
	local name = self:GetName();
	_G[name.."Left"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Up");
	_G[name.."Middle"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Up");
	_G[name.."Right"]:SetTexture("Interface\\Buttons\\UI-Silver-Button-Up");
end

Scripts.OnEnter:
if(self.tooltipText ~= nil) then
	GameTooltip_AddNewbieTip(self, self.tooltipText, 1.0, 1.0, 1.0, self.newbieText);
end

Scripts.OnClick:
SetGuildRosterSelection(0);
if ( self:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SetGuildRosterShowOffline(self:GetChecked());
GuildStatus_Update();


if ( not GuildControlUI ) then
	UIParentLoadAddOn("Blizzard_GuildControlUI");
end
if ( GuildControlUI:IsShown() ) then
	GuildControlUI:Hide();
else
	GuildFramePopup_Show(GuildControlUI);
end

if ( not GuildControlUI ) then
	UIParentLoadAddOn("Blizzard_GuildControlUI");
end
GuildFramePopup_Toggle(GuildControlUI);
