Scripts.OnEnter:
if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_LEFT");
	GameTooltip:SetItemByID(self.itemID);
	self.showingTooltip = true;
end

if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetItemByID(self.itemID);
end
self.showingTooltip = true;

Scripts.OnLoad:
--Messy Pathwerk use to automatically update texcodors
local ratio = 100/276 --New Size/Texture Size
local texs = { self:GetNormalTexture(), self:GetPushedTexture(), self:GetHighlightTexture() };
for i,tex in pairs(texs) do
	local l,t,_,b,r = tex:GetTexCoord();
	tex:SetTexCoord( (r-l)*ratio ,l,t,b);
end

--start small
self:SetSize(70, 44);
self.creature:SetSize(100, 50);
self.creature:SetPoint("TOPRIGHT", self, "TOPRIGHT", 21, 8);

Scripts.OnEnter:
if self.description then
	GameTooltip:SetOwner(self);
	GameTooltip:SetText(self.description);
end

Scripts.OnClick:
EncounterJournal.isHeroic = not EncounterJournal.isHeroic;
if EncounterJournal.isHeroic then
	self:LockHighlight();
else
	self:UnlockHighlight();
end
EncounterJournal_Refresh();
