Scripts.OnEnter:
if self.tooltipTitle then
	WorldMapTooltip:SetOwner(self, "ANCHOR_LEFT");
	WorldMapTooltip:SetText(self.tooltipTitle,1,1,1);
	WorldMapTooltip:AddLine(self.tooltipText, nil, nil, nil, 1);
	WorldMapTooltip:Show();
	WorldMapTooltip.EJ_using = true;
end

Scripts.OnClick:
if WORLDMAP_SETTINGS.size ~= WORLDMAP_WINDOWED_SIZE then
	ToggleFrame(WorldMapFrame);
end

if not EncounterJournal:IsShown() then
	ToggleFrame(EncounterJournal);
end
EncounterJournal_ListInstances();
EncounterJournal_DisplayInstance(self.instanceID);
EncounterJournal_DisplayEncounter(self.encounterID);

Scripts.OnEnter:
if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_LEFT");
	GameTooltip:SetItemByID(self.itemID);
	self.showingTooltip = true;
end

if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_LEFT");
	GameTooltip:SetItemByID(self.itemID);
	self.showingTooltip = true;
end

if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetItemByID(self.itemID);
end
self.showingTooltip = true;

if self.description then
	GameTooltip:SetOwner(self);
	GameTooltip:SetText(self.description);
end

if self.tooltipTitle then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltipTitle,1,1,1);
	GameTooltip:AddLine(self.tooltipText, nil, nil, nil, 1);
	GameTooltip:Show();
end

Scripts.OnLoad:
self.textures = {
	expanded = { 
		up = {self.eLeftUp, self.eMidUp, self.eRightUp},
		down = {self.eLeftDown, self.eMidDown, self.eRightDown}
	},
	collapsed = { 
		up = {self.cLeftUp, self.cMidUp, self.cRightUp},
		down = {self.cLeftDown, self.cMidDown, self.cRightDown}
	},
}
self.tex = self.textures.collapsed;

Scripts.OnShow:
local oldtex = self.textures.expanded;
if self:GetParent().expanded then
	self.tex = self.textures.expanded;
	oldtex = self.textures.collapsed;
else
	self.tex = self.textures.collapsed;
end

oldtex.up[1]:Hide();
oldtex.up[2]:Hide();
oldtex.up[3]:Hide();
oldtex.down[1]:Hide();
oldtex.down[2]:Hide();
oldtex.down[3]:Hide();


self.tex.up[1]:Show();
self.tex.up[2]:Show();
self.tex.up[3]:Show();
self.tex.down[1]:Hide();
self.tex.down[2]:Hide();
self.tex.down[3]:Hide();

Scripts.OnClick:
EncounterJournal_ToggleHeaders(self:GetParent())
self.tex.up[1]:Hide();
self.tex.up[2]:Hide();
self.tex.up[3]:Hide();
self.tex.down[1]:Hide();
self.tex.down[2]:Hide();
self.tex.down[3]:Hide();

if self:GetParent().expanded then
	self.tex = self.textures.expanded;
else
	self.tex = self.textures.collapsed;
end

self.tex.up[1]:Show();
self.tex.up[2]:Show();
self.tex.up[3]:Show();
self.tex.down[1]:Hide();
self.tex.down[2]:Hide();
self.tex.down[3]:Hide();

Scripts.OnMouseDown:
self.tex.up[1]:Hide();
self.tex.up[2]:Hide();
self.tex.up[3]:Hide();

self.tex.down[1]:Show();
self.tex.down[2]:Show();
self.tex.down[3]:Show();

Scripts.OnMouseUp:
self.tex.up[1]:Show();
self.tex.up[2]:Show();
self.tex.up[3]:Show();

self.tex.down[1]:Hide();
self.tex.down[2]:Hide();
self.tex.down[3]:Hide();

Scripts.OnClick:
EncounterJournal.isHeroic = not EncounterJournal.isHeroic;
if EncounterJournal.isHeroic then
	self.selected:Show();
else
	self.selected:Hide();
end
EncounterJournal_Refresh();
