Scripts.OnEnter:
if ( this.tooltipText ) then
	UIOptionsTooltip:SetOwner(this, "ANCHOR_RIGHT");
	UIOptionsTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end
if ( this.tooltipRequirement ) then
	UIOptionsTooltip:AddLine(this.tooltipRequirement, "", 1.0, 1.0, 1.0);
	UIOptionsTooltip:Show();
end

if ( this.tooltipText ) then
	UIOptionsTooltip:SetOwner(this, "ANCHOR_RIGHT");
	UIOptionsTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end
if ( this.tooltipRequirement ) then
	UIOptionsTooltip:AddLine(this.tooltipRequirement, "", 1.0, 1.0, 1.0);
	UIOptionsTooltip:Show();
end

Scripts.OnClick:
PlaySound("gsTitleOptionOK");
ResetTutorials();
TutorialFrame_HideAllAlerts();
TutorialFrameCheckButton:SetChecked(1);
--Check the show tutorials option
UIOptionsFrameCheckButton28:SetChecked(1);

PlayClickSound();
STATE_MULTI_ACTIONBAR_1 =  SHOW_MULTI_ACTIONBAR_1;
SHOW_MULTI_ACTIONBAR_1 = this:GetChecked();
MultiActionBar_Update();
UIParent_ManageFramePositions();

PlayClickSound();
STATE_AlwaysShowMultiBars = ALWAYS_SHOW_MULTIBARS;
ALWAYS_SHOW_MULTIBARS = this:GetChecked();
MultiActionBar_UpdateGridVisibility();

PlayClickSound();
SHOW_MULTI_ACTIONBAR_3 = this:GetChecked();
MultiActionBar_Update();
UIParent_ManageFramePositions();
UIOptionsFrame_UpdateDependencies();

Scripts.OnShow:
SetupFullscreenScale(this);
UIOptionsFrame_Load();
MultiActionBar_Update();
MultiActionBar_ShowAllGrids();
Disable_BagButtons();
UpdateMicroButtons();
